        to.query.activeFilters == JSON.stringify(this.activeFilters)
      ){

      console.log("Nothing changed",this.activeSearch,to.query.activeSearch)

    }else{

      // console.log("firstClick",this.firstClick)
      // if (!this.firstClick){
      //   return false;
      // }
      // if (!this.activeSearch && 
      //     !this.activeSort &&
      //     !this.activeSortDir &&
      //     !this.activeSortDir &&
      //     (!this.activeFilters || Object.keys(this.activeFilters).length == 0)
      //     ){
      //     console.log("First load or soemthing, skipping")
      // }

      if (Object.keys(to.query).length==0 && this.firstClick == true){ 
        this.activeSearch = ''
        this.activeSort = 'holdings_count'
        this.activeSortDir = 'desc'
        this.activeFilters = {}
        this.facet()
        return false

      }

      if (Object.keys(to.query).length==0){ return false}
      console.log("Changed!")
      if (to.query.activeSearch){
