      }
      if (to.query.activeFilters && Object.keys(to.query.activeFilters).length > 0){
        this.activeFilters = JSON.parse(to.query.activeFilters)  
      }
      

      console.log("this.activeSearch",this.activeSearch)
      console.log("this.activeSort",this.activeSort)
      console.log("this.activeSortDir",this.activeSortDir)
      console.log("this.activeFilters",this.activeFilters)

      window.setTimeout(()=>{
        this.facet()
      },50)

    }



    // Do something based on the new route
    }
  },



  methods: {
    async setFiltersFromPageFind() {

      let filters = await window.pagefind.filters()
      
      for (let key of Object.keys(filters)){

        let tmp = []
        tmp = filters[key]
        
        const entries = Object.entries(tmp);
        entries.sort((a, b) => b[1] - a[1]);


        
        this[key] = JSON.parse(JSON.stringify(entries))
        

      }




    },


    searchType(){


      window.clearTimeout(this.activeSearchTimeout)
      this.activeSearchTimeout = window.setTimeout(()=>{


        console.log("Searching ", this.activeSearch)
        this.facet()

      },750)



    },


    async facet(type,value,remove){

      this.firstClick=true
      if (type || value || remove){


        // {genres: {…}, language: {…}, lcsh: {…}}
        if (remove){

          this.activeFilters[type] = this.activeFilters[type].filter((v)=> { return (v != value)})

          if (this.activeFilters[type].length == 0){
            delete this.activeFilters[type]
          }
        }else{

          if (!this.activeFilters[type]){
            this.activeFilters[type] = [value]
          }else{
            if (!Array.isArray(this.activeFilters[type])){
              this.activeFilters[type] = [this.activeFilters[type]]
            }
            if (this.activeFilters[type].indexOf(value)){
              this.activeFilters[type].push(value)
            }
          }


        }



      }

      let sort = {}
      sort[this.activeSort] = this.activeSortDir 

      let searchKeyword = null

      if (this.activeSearch && this.activeSearch.trim() != ''){
        searchKeyword=this.activeSearch
      }

      let search = await window.pagefind.search(searchKeyword,{
        filters: this.activeFilters,
        sort: sort
      });

      for (let f in search.filters){

        let tmpFilter = {}

        for (let term in search.filters[f]){

          if (search.filters[f][term] > 0){
            tmpFilter[term] = search.filters[f][term]
          }
        }

        const entries = Object.entries(tmpFilter);
        entries.sort((a, b) => b[1] - a[1]);
        this[f] = JSON.parse(JSON.stringify(entries))


      }

    

      this.$router.push({ name: 'home', query: { activeSearch: this.activeSearch, activeSort:this.activeSort, activeSortDir: this.activeSortDir, activeFilters:JSON.stringify(this.activeFilters)   }  });

      this.processSearchIntoChunks(search)

    },


    async processSearchIntoChunks(search){
        
      this.totalResults = search.results.length
      if (this.totalResults == 0){ 
        this.activeVolumes = []
        this.moreVolumes = []
        return false

      }
      let chunks = []
      const chunkSize = 100;
      for (let i = 0; i <  search.results.length; i += chunkSize) {
          const chunk = search.results.slice(i, i + chunkSize);
          chunks.push(chunk)

      }

      let first = chunks.slice(0,1)[0]
      let rest = chunks.slice(1,chunks.length)

      let activeVolumesFirstPage = []
      for (let v of first){
        let r = await this.convertSearchItemIntoMeta(v) 
        activeVolumesFirstPage.push(r)
      }

      // activeVolumesFirstPage = activeVolumesFirstPage.sort((a, b) => b.holdings_count - a.holdings_count);

      this.activeVolumes = JSON.parse(JSON.stringify(activeVolumesFirstPage))
      this.moreVolumes = rest

      // console.log(rest)


    },

    async convertSearchItemIntoMeta(s){

      let data = await s.data()

      let hid = data.raw_url.replace('/','').replace('.html','')
      
      return this.volumesLookup[hid]

    },


    async loadMetadata() {


      let req = await fetch('browser_data.json')
      this.volumes = await req.json()
      this.returnStartingRandom()

      for (let v of this.volumes){
        this.volumesLookup[v.hid] = v
      }
      


    },

    returnStartingRandom(){

      let random = []

      random = this.volumes.filter((v) => { return (v.content_test == false && v.holdings_count && v.holdings_count > 25 && v.language == 'English')})

      // random = random.sort((a, b) => b.holdings_count - a.holdings_count);
      random = random.sort(() => 0.5 - Math.random());

      // Get sub-array of first n elements after shuffled
      random = random.slice(0, 10);


      this.activeVolumes = random

    },

    imageLoadError(event){

      // console.log(event,event.target)

    },

    setSort(s){

      if (this.activeSort == s){

        if (this.activeSortDir == 'asc'){
          this.activeSortDir = 'desc'
        }else{
          this.activeSortDir = 'asc'
        }

      }else{

        this.activeSort = s

        if (this.activeSort == 'holdings_count' || this.activeSort == 'scan_count'){
          this.activeSortDir = 'desc'
        }else{
          this.activeSortDir = 'asc'
        }



      }


      console.log(this.activeSort,this.activeSortDir)
      this.facet()


    },

    async show100More(){

      let toadd = this.moreVolumes.shift()
      for (let v of toadd){
        let r = await this.convertSearchItemIntoMeta(v) 
        this.activeVolumes.push(r)
      }

      



    }







  },

  async mounted() {



  },

  async created() {


    

    

    let to = window.setInterval(()=>{

      if (window.pagefind){
        window.clearInterval(to)
        this.setFiltersFromPageFind()
      }else{
        console.log("waiting for pagefind to load")
        // keep trying
      }


    },100)
    


    this.loadMetadata()


  }


}



</script>



